import { MockRequest, MockResponse } from 'xhr-mock';
import { exactMatch, fillInResponse, MockContext } from '../';

export const userAuth = (context: () => MockContext) => (
  req: MockRequest,
  res: MockResponse,
): MockResponse | undefined => {
  const data = {
    method: 'GET',
    url: {
      path: '/media-playground/api/token/user/impersonation',
      query: {},
    },
    headers: {
      accept: 'application/json, text/plain, */*',
    },
    body: null,
  };

  if (exactMatch(req, data)) {
    const clientId = '866af8a6-7d6d-458e-84fb-a1e44f648fec';
    const token =
      'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..Y3paoakNmCNS13BpRvi6ZdsPd6QYFF1v-kwMwX-qJmQ';
    const resdata = {
      status: 200,
      reason: 'OK',
      headers: {
        'access-control-allow-origin': '*',
        date: 'Tue, 20 Feb 2018 00',
        connection: 'keep-alive',
        'x-powered-by': 'Express',
        etag: 'W/2a6-OxGoXpRvoAR8dw98y9Z92A',
        'content-length': '678',
        'content-type': 'application/json; charset=utf-8',
      },
      body: JSON.stringify({ token: token, clientId: clientId }),
    };
    context().userContext.auth = { clientId: clientId, token: token };
    fillInResponse(res, resdata);
    return res;
  }

  return undefined;
};
